<style>
	.container {
		display: flex;
		margin: 0 auto;
		position: relative;
		align-items: center;
		padding-bottom: 40px;
		flex-direction: column;
		justify-content: center;
	}

	.header {
		width: 100%;
		display: flex;
		padding: 14px;
		align-items: center;
		background-color: var(--grey-50);
		border: 1px solid var(--primary-900);
		border-top-width: 0;
	}

	.home-link {
		padding: 5px;
		cursor: pointer;
		border-radius: 100%;
		transition: background-color 100ms;
	}

	.home-link:hover {
		background-color: var(--grey-200);
	}

	.header__title {
		font-size: 22px;
		font-weight: 600;
		margin-left: 17px;
	}

	.profile {
		width: 100%;
		padding: 20px;
		border-radius: 3px;
		margin-top: var(--space-48x);
		background-color: var(--grey-50);
		border: 1px solid var(--primary-900);
	}

	.profile-user {
		display: flex;
		align-items: center;
	}

	.profile__image {
		font-size: 13px;
		overflow: hidden;
		margin-right: 28px;
		border-radius: 100%;
		color: var(--primary-900);
		background-color: var(--grey-200);
		border: 1px solid var(--primary-900);
	}

	.profile__username {
		font-weight: 700;
		margin-bottom: 2px;
		letter-spacing: 0.4px;
	}

	.profile__joined {
		color: var(--grey-700);
		font-size: var(--text-14x);
	}

	.profile__bio {
		line-height: 25px;
		font-size: var(--text-16x);
		margin-top: var(--space-16x);
	}

	.profile-form {
		width: 100%;
		margin-top: var(--space-16x);
	}

	.profile-form__input {
		width: 100%;
		resize: none;
		overflow: auto;
		line-height: 28px;
		border-radius: 3px;
		padding: 12px 13px;
		letter-spacing: 0.5px;
		color: var(--grey-900);
		font-size: var(--text-16x);
		transition: box-shadow 100ms;
		border: 1px solid var(--primary-900);
	}

	.profile-form__input:focus {
		box-shadow: 0 0 0 1px var(--primary-900);
	}

	.profile-form__buttons {
		display: flex;
		align-self: center;
		justify-content: flex-end;
		margin-top: var(--space-16x);
	}

	.btn-sumbit {
		color: var(--grey-50);
		background-color: var(--primary-800);
	}

	.btn-sumbit:hover {
		background-color: var(--primary-900);
	}

	.btn-cancel {
		color: #000;
		background-color: transparent;
		margin-right: var(--space-16x);
	}

	.btn-cancel:hover {
		background-color: var(--grey-100);
	}

	.form-btn {
		border: none;
		padding: 8px 18px;
		font-size: var(--text-14x);
		transition: background-color 100ms;
	}

	.edit-btn {
		border: none;
		margin-left: 6px;
		color: var(--grey-600);
		background-color: transparent;
	}

	.edit-btn:hover {
		text-decoration: underline;
	}

	.profile-post {
		width: 100%;
		display: flex;
		border-radius: 3px;
		align-items: center;
		padding: 26px 20px 20px 20px;
		margin-top: var(--space-48x);
		justify-content: space-around;
		background-color: var(--grey-50);
		border: 1px solid var(--primary-900);
	}

	.profile-post__list {
		display: flex;
