	<form class="signin" on:submit|preventDefault={submitForm}>
		<div class="signin__wrapper">
			<InputLabel labelFor="email" labelContent="Email" labelDisabled={submitFormStatus} />

			<Input
				inputId="email"
				inputType="email"
				bind:inputValue={email}
				inputPlaceholder="Email"
				inputDisabled={submitFormStatus}
			/>
		</div>

		<div class="signin__wrapper signin__password-wrapper">
			<InputLabel labelFor="password" labelContent="Password" labelDisabled={submitFormStatus} />

			<Input
				inputIcon={true}
				inputId="password"
				bind:inputValue={password}
				inputPlaceholder="password"
				inputType={passwordInputType}
				inputDisabled={submitFormStatus}
			/>

			{#if statusInputPassword === "hide"}
				<button
					type="button"
					disabled={submitFormStatus}
					class="password-toggle-type"
					on:click={() => showPassword("show")}
				>
					<svg
						width="22"
						height="22"
						fill="none"
						stroke-width="2"
						viewBox="0 0 24 24"
						stroke-linecap="round"
						stroke-linejoin="round"
						xmlns="http://www.w3.org/2000/svg"
						stroke={submitFormStatus ? "#aeb0b4" : "#363a44"}
					>
						<circle cx="12" cy="12" r="3" />
						<path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z" />
					</svg>
				</button>
			{/if}

			{#if statusInputPassword === "show"}
				<button
					type="button"
					disabled={submitFormStatus}
					class="password-toggle-type"
					on:click={() => showPassword("hide")}
				>
					<svg
						width="22"
						height="22"
						fill="none"
						viewBox="0 0 24 24"
						xmlns="http://www.w3.org/2000/svg"
						stroke={submitFormStatus ? "#aeb0b4" : "#363a44"}
					>
						<path
							stroke-width="2"
							stroke-linecap="round"
							stroke-linejoin="round"
							d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-3.29-3.29m7.532 7.532l3.29 3.29M3 3l3.59 3.59m0 0A9.953 9.953 0 0112 5c4.478 0 8.268 2.943 9.543 7a10.025 10.025 0 01-4.132 5.411m0 0L21 21"
						/>
					</svg>
				</button>
			{/if}
		</div>

		<button type="submit" disabled={submitBtnDisabled} class="signin__btn">
			{submitBtnContet}
		</button>

		<p class="sign__msg">
			Don't have an account? ? <a href="/signup" class="signin__link">Sign Up</a>
		</p>
	</form>
</div>

<style>
	.container {
		display: flex;
		width: 1000px;
		align-items: center;
		justify-content: center;
		margin: 200px auto 0 auto;
		padding-bottom: var(--space-48x);
	}

	.signin {
		width: 35%;
		padding: 20px;
		border-radius: 3px;
		background-color: var(--grey-50);
	}

	.signin__password-wrapper {
		position: relative;
	}

	.signin__wrapper {
		width: 100%;
		display: flex;
		flex-direction: column;
		margin-bottom: var(--space-24x);
	}

	.signin__btn {
		width: 100%;
		padding: 8px;
		color: var(--grey-50);
		font-size: var(--text-16x);
		margin-top: var(--space-8x);
		transition: background-color 100ms;
		background-color: var(--primary-900);
		border: 1px solid var(--primary-500);
	}

	.signin__btn:hover {
		background-color: var(--primary-800);
	}

	.signin__btn:disabled {
		color: var(--grey-400);
		background-color: var(--grey-100);
		border: 1px solid var(--primary-300);
	}

	.signin__btn:disabled:hover {
		border: 1px solid var(--primary-300);
	}

	.signin__btn:focus {
		outline-offset: 1px;
		outline: 4px solid var(--primary-800);
	}

	.sign__msg {
		text-align: center;
		font-size: var(--text-14x);
		margin-top: var(--space-24x);
	}

	.signin__link {
		font-weight: 500;
		text-decoration: none;
		color: var(--light-blue-900);
	}

	.signin__link:hover {
		text-decoration: underline;
	}

	.signup-ilustration {
		width: 440px;
		height: 440px;
		margin-right: 60px;
		border-radius: 14px;
		border: 6px solid var(--primary-900);
	}

	.password-toggle-type {
		top: 0;
		right: 0;
		border: none;
		padding: 4px;
		position: absolute;
		border-radius: 100%;
		margin: 37px 13px 0 0;
		background-color: transparent;
	}

	.password-toggle-type:focus {
		outline-offset: -1px;
		outline: 2px solid var(--primary-900);
	}

	@media screen and (max-width: 1010px) {
		.container {
			width: 50%;
			margin-top: 140px;
			align-items: center;
			flex-direction: column;
		}

		.signin {
			width: 100%;
		}

		.signup-ilustration {
			display: none;
		}
	}

	@media screen and (max-width: 770px) {
		.container {
			width: 70%;
		}
	}

	@media screen and (max-width: 520px) {
		.container {
			width: 100%;
			padding: 0 16px;
		}

		.signin {
			width: 100%;
			padding: 18px 16px;
		}
	}
</style>
