	<form class="signup" on:submit|preventDefault={submitForm}>
		<div class="signup__wrapper">
			<InputLabel labelFor="email" labelContent="Email" labelDisabled={submitFormStatus} />

			<Input
				inputId="email"
				inputType="email"
				bind:inputValue={email}
				inputPlaceholder="email"
				inputDisabled={submitFormStatus}
			/>
		</div>

		<div class="signup__wrapper">
			<InputLabel labelFor="username" labelContent="Username" labelDisabled={submitFormStatus} />

			<Input
				inputType="text"
				inputId="username"
				bind:inputValue={username}
				inputPlaceholder="username"
				inputDisabled={submitFormStatus}
			/>
		</div>

		<div class="signup__wrapper signup__password-wrapper">
			<InputLabel labelFor="password" labelContent="Password" labelDisabled={submitFormStatus} />

			<Input
				inputIcon={true}
				inputId="password"
				bind:inputValue={password}
				inputPlaceholder="password"
				inputType={passwordInputType}
				inputDisabled={submitFormStatus}
			/>

			{#if passwordInputStatus === "hide"}
				<button
					type="button"
					disabled={submitFormStatus}
					class="password-toggle-type"
					on:click={() => setPassInputType("show")}
				>
					<svg
						width="22"
						height="22"
						fill="none"
						stroke-width="2"
						viewBox="0 0 24 24"
						stroke-linecap="round"
						stroke-linejoin="round"
						xmlns="http://www.w3.org/2000/svg"
						stroke={submitFormStatus ? "#aeb0b4" : "#363a44"}
					>
						<path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z" />
						<circle cx="12" cy="12" r="3" />
					</svg>
				</button>
			{/if}

			{#if passwordInputStatus === "show"}
				<button
					type="button"
					disabled={submitFormStatus}
					class="password-toggle-type"
					on:click={() => setPassInputType("hide")}
				>
					<svg
						width="22"
						height="22"
						fill="none"
						viewBox="0 0 24 24"
						xmlns="http://www.w3.org/2000/svg"
						stroke={submitFormStatus ? "#aeb0b4" : "#363a44"}
					>
						<path
							stroke-width="2"
							stroke-linecap="round"
							stroke-linejoin="round"
							d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-3.29-3.29m7.532 7.532l3.29 3.29M3 3l3.59 3.59m0 0A9.953 9.953 0 0112 5c4.478 0 8.268 2.943 9.543 7a10.025 10.025 0 01-4.132 5.411m0 0L21 21"
						/>
					</svg>
				</button>
			{/if}
		</div>

		<button disabled={submitBtnDisabled} type="submit" class="signup__btn">
			{submitBtnContent}
		</button>

		<p class="sign__msg">
			Have an account ? <a href="/signin" class="signup__link">Sign In</a>
		</p>
	</form>
</div>

<style>
	.container {
		width: 1000px;
		display: flex;
		align-items: center;
		justify-content: center;
		margin: 200px auto 0 auto;
		padding-bottom: var(--space-48x);
	}

	.signup {
		width: 35%;
		padding: 20px;
		border-radius: 3px;
		background-color: var(--grey-50);
	}

	.signup__password-wrapper {
		position: relative;
	}

	.signup__wrapper {
		width: 100%;
		display: flex;
		flex-direction: column;
		margin-bottom: var(--space-24x);
	}

	.signup__btn {
		width: 100%;
		padding: 8px;
		color: var(--grey-50);
		font-size: var(--text-16x);
		margin-top: var(--space-8x);
		transition: background-color 100ms;
		background-color: var(--primary-900);
		border: 1px solid var(--primary-500);
	}

	.signup__btn:hover {
		background-color: var(--primary-800);
	}

	.signup__btn:disabled {
		color: var(--grey-400);
		background-color: var(--grey-100);
		border: 1px solid var(--primary-300);
	}

	.signup__btn:disabled:hover {
		border: 1px solid var(--primary-300);
	}

	.signup__btn:focus {
		outline-offset: 1px;
		outline: 4px solid var(--primary-800);
	}

	.sign__msg {
		text-align: center;
		font-size: var(--text-14x);
		margin-top: var(--space-24x);
	}

	.signup__link {
		font-weight: 500;
		text-decoration: none;
		color: var(--light-blue-900);
	}

	.signup__link:hover {
		text-decoration: underline;
	}

	.signup-ilustration {
		width: 460px;
		height: 460px;
		margin-right: 60px;
		border-radius: 14px;
		border: 6px solid var(--primary-900);
	}

	.password-toggle-type {
		top: 0;
		right: 0;
		border: none;
		padding: 4px;
		position: absolute;
		border-radius: 100%;
		margin: 37px 13px 0 0;
		background-color: transparent;
	}

	.password-toggle-type:focus {
		outline-offset: -1px;
		outline: 2px solid var(--primary-900);
	}

	@media screen and (max-width: 1010px) {
		.container {
			width: 50%;
			margin-top: 140px;
			align-items: center;
			flex-direction: column;
		}

		.signup {
			width: 100%;
		}

		.signup-ilustration {
			display: none;
		}
	}

	@media screen and (max-width: 770px) {
		.container {
			width: 70%;
		}
	}

	@media screen and (max-width: 500px) {
		.container {
			width: 100%;
			padding: 0 16px;
		}

		.signup {
			width: 100%;
		}
	}
</style>
